import pytest
from app import app as flask_app
from bson import ObjectId
import json
from unittest.mock import patch

BASE64_IMAGE = "data:image/png;base64," \
               ""

INVALID_BASE64_IMAGE = "data:image/png;base64, 5QrY2/lm+lcyujNAGSdu2g=="


@pytest.fixture
def app():
    app = flask_app
    app.config['TESTING'] = True
    return app


@pytest.fixture
def client(app):
    return app.test_client()


# Mocking MongoDB Connection
class MockedMongoClient:
    def __init__(self, *args, **kwargs):
        pass

    @property
    def SnapChat(self):
        return self

    @property
    def User(self):
        return self

    @property
    def communication(self):
        return self

    def find_one(self, *args, **kwargs):
        return {'_id': ObjectId(), 'username': 'testuser'}

    def insert_one(self, *args, **kwargs):
        return type('obj', (object,), {'inserted_id': ObjectId()})

    def find(self, *args, **kwargs):
        return iter([{'_id': ObjectId(), 'username': 'testuser', 'message': 'test', 'timestamp': '2023-12-19'}])

    def delete_one(self, *args, **kwargs):
        pass


@pytest.fixture
def mocked_mongo(monkeypatch):
    monkeypatch.setattr('app.MongoClient', MockedMongoClient)


def test_home(client):
    response = client.get('/')
    assert response.status_code == 200
    assert b'login' in response.data


def test_login(client, mocked_mongo):
    response = client.post('/login', data={'username': 'testuser'}, follow_redirects=True)
    assert response.status_code == 200
    assert b'index' in response.data


def test_main(client, mocked_mongo):
    client.post('/login', data={'username': 'testuser'}, follow_redirects=True)
    response = client.get('/main')
    assert response.status_code == 200
    assert b'index' in response.data


def test_get_messages(client, mocked_mongo):
    response = client.get('/get_messages')
    assert response.status_code == 200
    data = json.loads(response.data)
    assert isinstance(data, list)


def test_delete_message(client, mocked_mongo):
    response = client.post('/delete_message/507f1f77bcf86cd799439011', follow_redirects=True)
    assert response.status_code == 200
    data = json.loads(response.data)
    assert data['status'] == 'success'


def test_post_message(client, mocked_mongo):
    with client:
        client.post('/login', data={'username': 'testuser'}, follow_redirects=True)
        response = client.post('/post_message', data={
            'message': 'Hello, world!',
            'image': BASE64_IMAGE
        })
        assert response.status_code == 200
        data = json.loads(response.data)
        assert 'message' in data
        assert data['message'] == 'Hello, world!'


def test_logout(client, mocked_mongo):
    with client:
        client.post('/login', data={'username': 'testuser'}, follow_redirects=True)

        response = client.get('/logout', follow_redirects=True)

        assert response.status_code == 200
        assert b'login' in response.data

        with client.session_transaction() as session:
            assert 'username' not in session


def test_convert_to_sketch_success(client, mocked_mongo):
    with client, patch('app.cv2') as mock_cv2:
        mock_cv2.imdecode.return_value = "mock_image"
        mock_cv2.cvtColor.return_value = "mock_gray_image"
        mock_cv2.GaussianBlur.return_value = "mock_blurred_image"
        mock_cv2.Canny.return_value = "mock_edges"
        mock_cv2.imencode.return_value = [None, b'mock_encoded_image']

        client.post('/login', data={'username': 'testuser'}, follow_redirects=True)
        response = client.post('/convert_to_sketch', data={
            'message': 'Test Sketch',
            'image': BASE64_IMAGE
        })
        assert response.status_code == 200
        data = json.loads(response.data)
        assert 'photo' in data
        assert data['photo'].startswith('data:image/jpeg;base64,')


def test_convert_to_cartoon_success(client, mocked_mongo):
    with client, patch('app.cv2') as mock_cv2:
        mock_cv2.imdecode.return_value = "mock_image"
        mock_cv2.cvtColor.return_value = "mock_gray_image"
        mock_cv2.medianBlur.return_value = "mock_blurred_image"
        mock_cv2.adaptiveThreshold.return_value = "mock_edges"
        mock_cv2.bilateralFilter.return_value = "mock_filtered_color"
        mock_cv2.bitwise_and.return_value = "mock_cartoon"
        mock_cv2.imencode.return_value = [None, b'mock_encoded_image']

        client.post('/login', data={'username': 'testuser'}, follow_redirects=True)
        response = client.post('/convert_to_cartoon', data={
            'message': 'Test Cartoon',
            'image': BASE64_IMAGE
        })
        assert response.status_code == 200
        data = json.loads(response.data)
        assert 'photo' in data
        assert data['photo'].startswith('data:image/jpeg;base64,')
